<?php $linkvideo=file_get_contents('php://input');function displayRecursiveResults($arrayObject){$return="";foreach($arrayObject as $key=>$data){if(is_array($data)){displayRecursiveResults($data);}elseif(is_object($data)){displayRecursiveResults($data);}else{if($key=="url"){$return.="<a href='".$data."'>DOWNLOAD</a>";}if($key=="name"){$return.=" : ".$data;}if($key=="subname"){$return.=" (".$data.") <hr>";}}}echo $return;}$url="https://ytdl.ga/handler.php?url=".$linkvideo;$cUrl=curl_init($url);curl_setopt_array($cUrl,[CURLOPT_CUSTOMREQUEST=>'GET',CURLOPT_RETURNTRANSFER=>1,]);$result=curl_exec($cUrl);curl_close($cUrl);$resultado=json_decode($result);echo "<img src=".$resultado->thumb." alt='thumbnail' width='250' height='150'/>";echo "<br/>";displayRecursiveResults($resultado->url);
